
IF NOT EXISTS(SELECT * FROM Template WHERE Name='PurchaseOrderEmail')
BEGIN
INSERT INTO Template (Name,Subject,Body,IsActive) VALUES('PurchaseOrderEmail','${POFaxFrom}  PO:  ${PurchaseOrderNumber}','<html>  <head>  <meta charset="utf-8">  <title>Fax P.O.</title>  <style>   body{font:10pt/11pt Courier, monospace;}   </style>    </head>    <body>  <div>  <table width="600" border="0" cellpadding="0" cellspacing="0">    <tr>      <td>TO:</td>      <td>${TalkedTo}</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>COMPANY:</td>      <td>${VendorName}</td>      <td>${VendorNumber}</td>    </tr>    <tr>      <td>FAX:</td>      <td>${VendorFax}</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>FROM:</td>      <td>${POFaxFrom}</td>      <td>${IssueDateTime}</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>&nbsp;</td>      <td colspan="2">&quot;THE GOLDEN RULE&quot;: This PO covers ONLY the service type     and dollar amount stated during the dispatch conversation. If the amount of services should need to be changed,     please call for AUTHORIZATION while the driver is still on the scene with the member.</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>&nbsp;</td>      <td colspan="2">=========================================================================</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>PO      #      :</td>      <td>${PurchaseOrderNumber}</td>      <td>&nbsp;&nbsp;&nbsp;Opened      By: ${CreateBy}</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>SERVICE:</td>      <td>${ProductCategoryName}</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>ETA:</td>      <td colspan="2">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;${ETAMinutes}      Minutes &nbsp;&nbsp; Safe:&nbsp;&nbsp;${Safe} Member Pay:&nbsp;&nbsp;${MemberPay}</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>&nbsp;</td>      <td colspan="2">========================= CUSTOMER      and LOCATION&nbsp;=========================</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>Name:</td>      <td>${MemberName}</td>      <td>Member #:&nbsp;&nbsp;&nbsp;&nbsp;${MembershipNumber}</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>Telephone#:</td>      <td colspan="2">${ContactNumber}&nbsp;&nbsp;&nbsp;Alternate: ${AlternateContact}</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>Loc:</td>      <td>${ServiceLocationDescription}</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>Cross:</td>      <td>${ServiceLocationAddress}</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>City:</td>      <td colspan="2">${ServiceLocationCityState} Zip: ${ServiceLocationPostalCode}</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>&nbsp;</td>      <td colspan="2">========================= DESTINATION INFORMATION&nbsp;=======================</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>Dest:</td>      <td>${DestinationDescription}</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>Address:</td>      <td>${DestinationAddress}</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>City:</td>      <td colspan="2">${DestinationCityState} Zip: ${DestinationPostalCode}</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>&nbsp;</td>      <td colspan="2">========================= VEHICLE INFORMATION&nbsp;===========================</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>Year:</td>      <td colspan="2">${VehicleYear}&nbsp;&nbsp;&nbsp;&nbsp;Make: ${VehicleMake} Model: ${VehicleModel}</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>Desc:</td>      <td>${VehicleDescription}</td>      <td>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Color: ${VehicleColor}</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>License:</td>      <td colspan="2">${VehicleStateLicense}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Vin#: ${VehicleVin}</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>Chassis:</td>      <td colspan="2"><table width="512" border="0" cellpadding="0" cellspacing="0">        <tr>          <td>${VehicleChassis}&nbsp;&nbsp;&nbsp;</td>          <td>Length:&nbsp;&nbsp;&nbsp;${VehicleLength}</td>          <td>Eng:&nbsp;&nbsp;&nbsp;${VehicleEngine}</td>          <td>Class:&nbsp;&nbsp;&nbsp;${VehicleClass}</td>        </tr>      </table></td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>&nbsp;</td>      <td colspan="2">=========================================================================</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td colspan="3">For questions about this dispatch, please  call ${VendorCallback} Option 1. For Billing questions, call ${VendorBilling}, or if you submit claims online at vendor.coach-net.com, you can also check your payment status online.</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td>&nbsp;</td>      <td>&nbsp;</td>      <td>&nbsp;</td>    </tr>    <tr>      <td colspan="3">*** END OF TRANSMISSION*** SENT BY ${POFaxFrom} ${IssueDateTime}</td>    </tr>  </table>  </div>  </body>  </html>',1)
END